function submitNewMessage(){$("#submit_message").keydown(function(e){if(13==e.keyCode){var a=e.target.value,o=$("[data-chatroom]").data().chatroom;App["room"+o].setChatroomId(o),App["room"+o].send({message:a}),$('[data-textarea="message"]').val(""),$(".panel-body").stop().animate({scrollTop:$(".panel-body")[0].scrollHeight},800)}})}$(document).ready(function(){submitNewMessage()});